  }

  table = {
    line: 5,
    cell: 5
  }

  btnTable() {
    this.restoreSelection();
    var r = void 0,
      rowNum = this.table.line,
      colNum = this.table.cell,
      c = void 0;
    var html =
      '<table style="border-collapse:collapse;" width="300px" cellpadding="1" cellspacing="1">';
    for (r = 0; r < rowNum; r++) {
      html += "<tr>";
      for (c = 0; c < colNum; c++) {
        html += "<td style='border:1px solid #eee;padding:5px 3px;'>&nbsp;</td>";
      }
      html += "</tr>";
    }
    html += "</table><p><br></p>";
    this.btnCommand('insertHTML', html);
    this.setTableDrag();
  }

  btnKeypress(e) {
    this.isImage = false;
    this.isTable = false;
    if (e.keyCode == 8 && e.srcElement.innerHTML == "") {
      this.btnCommand('insertHTML', '<p>&nbsp;</p>');
    }
  }
  kenter(e) {
    this.saveSelection(e);
  }
  closeModel() {
    this.icons.forEach(v => { v.select = false });
    this.isModle = "";
    this.isImage = false;
    this.isTable = false;
  }
  mousedown(e) {
    this.closeModel();
  }
  getoff(img) {
    var left = img.offsetLeft;
    var top = img.offsetTop;
    var current = img.offsetParent;

    while (current && current.getAttribute('data') !== 'editor') {
      left += current.offsetLeft;
      top += current.offsetTop;
      current = current.offsetParent;
    }
    return { left, top };
  }

  getCurrentRange(e) {
    // 获取当前的选区
    const selection = window.getSelection() as Selection
    if (selection.rangeCount === 0) {
      return this.selectedRange && this.selectedRange
    }
    let range = selection.getRangeAt(0)
    if (String(range.commonAncestorContainer.parentElement.contentEditable) != 'true') {
      return this.selectedRange;
    }
    return range;
  }

  // 保存当前的选区
  saveRange(range: any) {
    if (range) {
      // 保存已有选区
      this.selectedRange = range;
      return;
    }
    var selection = window.getSelection();
    if (selection.rangeCount === 0) {
      return;
    }
    var range = selection.getRangeAt(0);
    if (this.$refs.editor.contains(range.commonAncestorContainer)) {
      this.selectedRange = range;
    }
  }

  setTableDrag() {
    this.$nextTick(() => {
      this.$refs.editor.querySelectorAll('table').forEach(item => {
        item.onmousemove = e => {
          let { clientWidth, clientHeight, offsetHeight, offsetWidth } = item;
          let { left, top } = this.getoff(item);
          this.isTable = true;
          this.$refs.tabledrag.style.left = `${clientWidth + left - 15}px`;
          this.$refs.tabledrag.style.top = `${clientHeight + top - 15}px`;
          let disX = 0;//鼠标按下时光标的X值
          let disW = 0; //拖拽前div的宽
          this.$refs.tabledrag.onmousedown = e => {
            let ev = e || window.event;
            disX = ev.clientX; // 获取鼠标按下时光标x的值
            disW = clientWidth; // 获取拖拽前div的宽
            document.onmousemove = (e) => {
              let ev = e || window.event;
              let W = ev.clientX - disX + disW;
              this.$refs.imgoff.style.left = W + 1 + 'px';// 拖拽后物体的宽
              this.$refs.imgoff.style.top = item.clientHeight + 'px';// 拖拽后物体的高
              item.style.width = W + 'px';
            }
            document.onmouseup = () => {
              document.onmousemove = null;
              document.onmouseup = null;
              this.isImage = false;
            }
          }
        }
      })
    })
  }

  setImgDrag() {
    this.$nextTick(() => {
      this.$refs.editor.querySelectorAll('img').forEach(item => {
        item.onclick = e => {
          let { clientWidth, clientHeight } = item;
          let { left, top } = this.getoff(item);
          this.isImage = false;
          this.$nextTick(() => {
            this.isImage = true;
            let sctop = this.$refs.editor.scrollTop;
            let oDrag = this.$refs.imgoff.querySelector('svg');
            this.$refs.imgoff.style.width = clientWidth + 2 + 'px';
            this.$refs.imgoff.style.height = clientHeight + 2 + 'px';
            this.$refs.imgoff.style.left = left - 1 + 'px';
            this.$refs.imgoff.style.top = top - sctop - 1 + 'px';

            let disX = 0;//鼠标按下时光标的X值
            let disW = 0; //拖拽前div的宽
            oDrag.onmousedown = e => {
              let ev = e || window.event;
              disX = ev.clientX; // 获取鼠标按下时光标x的值
              disW = clientWidth; // 获取拖拽前div的宽
              document.onmousemove = (e) => {
                let sctop = this.$refs.editor.scrollTop;
                let ev = e || window.event;
                let W = ev.clientX - disX + disW;
                this.$refs.imgoff.style.width = W + 2 + 'px';// 拖拽后物体的宽
                this.$refs.imgoff.style.height = item.clientHeight + 2 + 'px';// 拖拽后物体的高
                this.$refs.imgoff.style.top = top - sctop - 1 + 'px';
                item.style.width = W + 'px';
              }
              document.onmouseup = () => {
                document.onmousemove = null;
                document.onmouseup = null;
                this.isImage = false;
              }
            }

          })
        }
      })
    })
  }

  upfileImage(type) {
    this.restoreSelection();
    if (type == 2) {
      let html = `<img style="max-width:100%;border-radius:4px;" src=${this.upImageFile}>`;
      this.btnCommand('insertHTML', html);
      this.upImageFile = "";
      this.setImgDrag();
    } else {
      let input = this.$refs.input
      let files = input[0].files
      this.upFile(files[0]).then(res => {
        let html = `<div style="display:inline-flex rel"><img style="max-width:100%;border-radius:4px;" src=${res}></div>`;
        this.btnCommand('insertHTML', html);
        this.setImgDrag();
      })
    }
  }

  // 鼠标事件
  mouseup(e) {
    this.saveSelection(e);
  }
  btnAction(item) {
    this.closeModel();
    if (item.type != 'cmd') {
      this.saveRange()
    }
    if (item.type == 'cmd') {
      this.btnCommand(item.name);
    } else if (item.name == 'quote') {
      let html = `<div style="padding: 15px;margin: 10px 0;border-left: 5px solid #00a3cf;background: #f6f6f6;"><p><br></p></div>`;
      this.btnCommand('insertHTML', html);
      var p = document.createElement('p');
      p.innerHTML = '<br>';
      this.$refs.editor.appendChild(p)
    } else if (['fc', 'bc', 'fs', 'image', 'header', 'link', 'table'].includes(item.name)) {
      item.select = true;
      this.isModle = item.name;
    } else if (item.name == 'insertOrderedList') {
      let html = `<ol><li style="margin-left: 15px;list-style-type: inherit;"></li></ol>`;
      this.btnCommand('insertHTML', html);
    } else if (item.name == 'insertUnorderedList') {
      let html = `<ul><li style="margin-left: 15px;list-style-type: inherit;"></li></ul>`;
      this.btnCommand('insertHTML', html);
    } else if (item.name == 'formatmatch') {
      // this.$emit('input', this.$refs.editor.innerText)
      this.$emit('update:modelValue', this.$refs.editor.innerText);
    } else if (item.name == 'fullscreen') {
      if (this.isScreen) {
        this.isScreen = false;
      } else {
        this.isScreen = true;
      }
    }
  }
  btnFontSize(e) {
    if (e.target.dataset.fs) {
      this.restoreSelection()
      this.btnCommand('fontsize', parseInt(e.target.dataset.fs));
    }
  }
  btnHeader(e) {
    if (e.target.dataset.item) {
      this.restoreSelection()
      this.btnCommand('formatBlock', e.target.dataset.item);
    }
  }
  btnColor(e) {
    if (e.target.dataset.color) {
      this.restoreSelection()
      this.btnCommand(this.isModle == 'fc' ? 'foreColor' : 'hiliteColor', e.target.dataset.color);
    }
  }
  btnLink(e) {
    if (this.selectedRange && this.selectedRange.toString()) {
      this.restoreSelection();
      var innerDom = this.selectedRange.extractContents();
      var link = document.createElement("a");
      link.setAttribute("href", this.linkText);
      link.style.color = "#409eff";
      link.target = "_blank"
      link.innerHTML = this.selectedRange.toString();
      link.append(innerDom);
      this.selectedRange.insertNode(link);
    } else {
      this.restoreSelection();
      this.btnCommand('CreateLink', this.linkText);
    }
    this.closeModel();
    this.linkText = "";
  }
  changeText(e) {
    // this.$emit('input', e.srcElement.innerHTML)
    this.$emit('update:modelValue', e.srcElement.innerHTML);
  }
  btnCommand(cmd, html: any = "") {
    html = html ? html : false;
    // this.restoreSelection();
    // this.$refs.editor.focus();
    document.execCommand(cmd, false, html);
    this.closeModel();
    this.setImgDrag();
  }
  blurFunc() {
    this.isChange = true;
  }


  saveSelection(e) {
    this.selectedRange = this.getCurrentRange(e);
    // console.log(this.selectedRange)
  }
  restoreSelection() { //重置为上个range
    var selection: any = window.getSelection();
    if (this.selectedRange) {
      try {
        selection.removeAllRanges();
      } catch (ex) {
        (document.body as any).createTextRange().select();
        (document as any).selection.empty();
      };
      selection.addRange(this.selectedRange);
    }
    this.$refs.editor.focus();
  }

  getSelectionHTML() {
    if (window.getSelection) {
      var sel: any = window.getSelection();
      if (sel.rangeCount > 0) {
        return sel;
      }
    }
  }
  // 格式化 hex 颜色值
  parseColor(hexStr) {
    if (hexStr.length === 4) {
      hexStr = '#' + hexStr[1] + hexStr[1] + hexStr[2] + hexStr[2] + hexStr[3] + hexStr[3]
    } else {
      return hexStr
    }
  }
  // RGB 颜色 转 HEX 颜色
  rgbToHex(r, g, b) {
    let hex = ((r << 16) | (g << 8) | b).toString(16)
    return '#' + new Array(Math.abs(hex.length - 7)).join('0') + hex
  }
  // HEX 转 RGB 颜色
  hexToRgb(hex: any) {
    hex = this.parseColor(hex)
    let rgb: any = []
    for (let i = 1; i < 7; i += 2) {
      rgb.push(parseInt('0x' + hex.slice(i, i + 2)))
    }
    return rgb
  }
  // 计算渐变过渡颜色
  gradient(startColor, endColor, step) {
    // 讲 hex 转换为 rgb
    let sColor = this.hexToRgb(startColor)
    let eColor = this.hexToRgb(endColor)

    // 计算R\G\B每一步的差值
    let rStep = (eColor[0] - sColor[0]) / step
    let gStep = (eColor[1] - sColor[1]) / step
    let bStep = (eColor[2] - sColor[2]) / step


    let gradientColorArr: any = []
    // 计算每一步的hex值
    for (let i = 0; i < step; i++) {
      gradientColorArr.push(this.rgbToHex(parseInt(rStep * i + sColor[0]), parseInt(gStep * i + sColor[1]), parseInt(bStep * i + sColor[2])))
    }
    gradientColorArr.push(endColor)
    return gradientColorArr
  }

  @Watch('modelValue')
  changeValue(newVal, oldVal) {
    if (this.isChange) {
      this.innerText = this.value;
      this.setImgDrag();
      this.setTableDrag();
    }
  }

  mounted() {
    this.colors.hex.forEach(v => {
      this.colors.plan.push(this.gradient(v[0], v[1], 5))
    })
    if (this.value && !this.innerText) {
      this.innerText = this.value;
      this.setTableDrag();
      this.setImgDrag()
    }
  }

}
</script>

